INSERT INTO song_collection VALUES ('rec-49c2625786784edb91016e26f0047bb1','A La Puerta Del Cielo','s,  t, d r m f s l','Plagal do Major','Low ti, Harmonic Function I-V','q,n,h','nn’qn’qn’qq(*,2) qn’nq’qn’nq(3)   nn’qn’qq’h(f)','s,ddr/mfr/srr/mr (*) s,ddr/mfr/sff/mr (2) ssl/fmf/mmr/rdr (3) s,ddr/mfs/dt,/d (f)','2/4','7DD273CD-0BBE-48E0-B775-936BA0A37AD6.jpg',NULL,'2.  A los niños que duerman Dios benedice	      A las madres que velan Dios las asiste.	      Refrain			English Translation1.	At the gate of heaven little shoes they are selling,	       For the little bare-footed angels there dwelling.	       Slumber my baby, slumber my baby	       Slumber my baby, arru, arru.2.	God will bless children who go to sleep.    	       God will help mothers who pray.	       Refrain',NULL,NULL,'d-t-,d','4.4.4.4.','a.av.b.av','a.a.b.c','Verse-Refrain','Lullaby','New Mexico, Basque','Spanish','A-C',NULL,'Locke, Eleanor G.  Sail Away: 155 American Folksongs to sing read and play.  Boosey & Hawkes, 1955, 100.',NULL,'A LA PUERTA DEL CIELO.docx');
