INSERT INTO Master_Copies VALUES ('rec-4028625dc539433dac17dba3cc8fe313','Jack be Nimble',NULL,NULL,'Triple meter, High and low (comparitive)','j,§,qe',NULL,NULL,'6/8','1F6E9432-1C49-468D-B7B9-E2DC9E88348C.jpg',NULL,NULL,'Scattered self-space, standing','Swing arms while chanting.  One child (teacher first) skips around among the other children, stopping in front of someone on “candlestick.”  Those two children perform the motions in the last two lines, and the “new one” skips around and chooses another child as all repeat the rhyme.',NULL,'2.2.2.2.',NULL,'a.b.c.d.','Single strophe with spoken text','Nursery Rhyme',NULL,'English',NULL,NULL,'Miragliotta, Nikki MSU Kodály Institute, 2015',NULL,'Jack be nimble.docx');
