INSERT INTO Master_Copies VALUES ('rec-ff61400c9fd24c7bbe55a62e1287bbc0','Little Bird, Little Bird','drmfs','Plagal do pentachord','Mixed meter, fa, Melodic Sequence','q,n,Q,h,je',NULL,'ssm/ssmm/sl/sm (*,3) ffr/ffrr/sl/sm (2) m/sfmrd/s (4,f) s/fs/ms/rrr/r (5) s/ms/rs/ddd (5)','Mixed','C4610031-440A-468E-AE80-5506423B8B7D.jpg',NULL,NULL,'Standing circle, one it as “bird” standing outside of circle.','All stand with hands held and arms raised.  Child “bird” flies through each raised window.  At end of song, they tap a new “bird” on the shoulders and take their spots.  Begin again.','s-f-m','4.4.4.4.4.4.4.','a.b.a.c.d.e.c.','a.a.a.b.c.c.b.','Single strophe','Animal song (songs that feature animals)','South Carolina','English',NULL,'melodic sequence in the d and e phrases (fs-ms-r…ms-rs-d)','Johnston, Richard. Folk Songs North America Sings.',NULL,'Litte bird, little bird.docx');
