INSERT INTO Master_Copies VALUES ('rec-17925f8fead94752b42fee5c285604a5','Little Sally Walker (1)','drmfsl','Authentic do pentachord','Community builder (dance and game)','q,n,m,M, u',NULL,'m/sssml/smf/sfmrd (2)','2/4','A14F7019-8DAE-4483-B7D5-8E68E4373463.jpg','D129B9D5-2E64-4C00-B93F-204B233D79AA.jpg',NULL,'Standing circle, one player in center','Center player walks around the inside of the circle until “stopped in front of me.” The center player faces someone in the outside circle and makes a motion: person in outer circle copies motion.  On “switch,” the 2 players switch places (original leader is STILL the leader).  The chose person become the new “Sally.” Game repeats','m-f-s/s-f-m','4.4.4.4.','a.av.b.bv.','a.b.c.c.','Single strophe','Ring Play','African-American','English','A-C',NULL,'Brown, Nysa. UST Kodály Institute Materials Packet, 2013 from Bess Lomax Hawes and Bessie Jones Step it Down.',NULL,'Little sally walker (vs.2).docx');
