INSERT INTO Master_Copies VALUES ('rec-02520feaa96049c68499350055e2ec1e','My Singing Bird','s,l,  drmfsl','Mixolydian','Mixolydian, Songtale, Two eighth note anacrusis, dotted quarter eighth',NULL,NULL,NULL,'4/4','E63E7C7F-87DF-40AD-BFFD-586925EA8EA2.jpg',NULL,'If I could lure my singing bird From his own cozy nest If I could catch my singing bird I would warm him on my breast And on my heart my singing bird would sing itself to restAaa…..would sing itself to rest.',NULL,NULL,NULL,'4.4.4.4.','a.a.b.a.','a.a.b.a.','Verse-Refrain','Courting',NULL,'English','F-A',NULL,'Erdei, Ida, Faith Knowles and Denise Bacon. My Singing Bird. 150 Folk Songs from the Anglo-American, African-American, English, Scottish and Irish Traditions, 111.',NULL,'my singing bird.docx');
