INSERT INTO Master_Copies VALUES ('rec-3af0b79831984565a085b7690dbf5281','Peackock Song','s,l,t,drmfs','Natural minor','Natural minor, Melodic Sequence','q,h,w,O',NULL,'descending 5ths: ml,/frt, (*,4) rs,/sfm (2,5)','Cut','9F8FDDAA-14EE-44C1-868B-527ABEB6615A.jpg',NULL,NULL,NULL,NULL,'m-l,-f/r-s,-s','2.2.4.2.2.4.','a.b.c.a.b.c.','a.b.c.a.b.c.','Single strophe','Animal song (songs that feature animals)','South Carolina','Vocables','A-C','“An old [African-American] woman in South Carolina told of all the animals holding a meeting.  They elected the peacock to be queen.  She sang an acknowledgment, spoke with music, her appreciation of the honor conferred on her.  Thus we have the Coo-Coo murmur, moan and cry.” From Kodaly.hnu.edu','kodaly.hnu.edu.  Carl Sandburg, The American Songbag, New York: Harcourt, Brace & Co. 1927.',NULL,NULL);
