INSERT INTO Master_Copies VALUES ('rec-d0cfd2f904d54c478b2713d309acb729','Pizza Pizza Daddy-O','m  sl','Authentic mi tritone','Community builder (name game)',NULL,NULL,NULL,'4/4','6772B70A-4FED-4C7A-AC48-1BCD965761F0.jpg',NULL,NULL,'Standing circle, one “it”','“Pizza pizza daddy O” feet motions: feet out to side, feet together with R foot in front, feet out to side, feet together with L foot in front, both feet together.“It” in middle calls actions, all act out together.  Person in middle closes their eyes on “let’s end it” and spins, stops on “o!”Person who is being pointed to is new “it”',NULL,NULL,NULL,NULL,NULL,NULL,'African-American','English','F-A',NULL,'Brown, Nyssa.  Materials, Level III, UST 2012.  University of St. Thomas, St. Paul MN, 2012.  Notation from Beth’s music notes, http://www.bethsnotesplus.com/2013/02/pizza-pizza-daddy-o.html, accessed June 2015.',NULL,'Pizza Pizza Daddy-o.docx');
INSERT INTO Master_Copies VALUES ('rec-f1c70d31b10a42e48587942fc2cdb902','Plainsies Clapsies','m  sl','Authentic mi tritone','la, Quarter and two eighth notes','q,n','qq’qq(*,3) nn’qq(2) nq’nq(4,5) nn’nq(6) ','sl/sm (*,3) ssll/sm (2) ssl/ssm (4,5) ssll/ssm (f)','2/4','8A20E2DA-E91C-4C8E-9C27-8FD7B52C746A.jpg',NULL,NULL,'Scattered, with balls','Measure one: toss a ball in the air and catch it.  Measure two: toss the ball and clap before you catch it.  Measure three: toss the ball and twirl hands before catching.  Measure four: toss then touch your back.  Continue song dramatizing the words but tossing the ball each time.  The last time bounce the ball under your leg.','s-l-s','2.2.2.2.2.2.','a.ai.a.b.b.c.','a/b/a/c/c/d/','Single strophe','Action (songs that have actions)',NULL,'English','A-C',NULL,'Eisen, Ann and Lamar Robertson.  An American Methodology.  Sneaky Snake Publications, 2002 (Revised Edition),332.	           ',NULL,'PLAINSIES CLAPSIES.docx');
